by_name.map:
function(doc, meta)
{
  if (doc.type && doc.type == "division")
  {
    emit(doc.divisionName, null);
  }
}

by_id.map:
function(doc, meta)
{
  if (doc.type && doc.type == "division")
  {
    emit(doc.divisionId, null);
  }
}

unplayed.map:
function (doc, meta) {
  if (doc.type && doc.type == "fixture" && doc.fixtureDate && doc.homeGoals == null) {
    var dateComponents = doc.fixtureDate.split("/");

  	emit([dateComponents[2],dateComponents[1],dateComponents[0],doc.divisionId,doc.homeTeamId], null);
  }
}

